import React, { useState, useEffect } from "react";
import axios from "axios";
import Swapi from "./Swapi";

const Main = () => {
  const [hero, setHero] = useState("");
  const [heroId, setHeroId] = useState(1);
  const [starships, setStarships] = useState([]);

  let ships = [];

  useEffect(() => {
    const getHero = async (id) => {
      let url = `https://swapi.dev/api/people/${id}/`;
      await axios.get(url).then(function (res) {
        setHero(res.data.name);
        ships = [];
        res.data.starships.forEach(async (starship) => {
          let url = `https://swapi.dev/api/people/${starship}/`;
          await axios.get(url).then(function (res1) {
            ships.push(res1.data.name);
          });
          Promise.all();
          setStarships(ships);
        });
      });
    };
    getHero(heroId);
  }, [heroId]);

  const onChange = (e) => {
    setHeroId(e.target.value);
  };

  return (
    <div className="main">
      <input
        id="number"
        type="number"
        onChange={onChange}
        value={heroId}
      ></input>
      <Swapi hero={hero} starships={starships} />
    </div>
  );
};

export default Main;
