import React, { useState, useEffect } from "react";
import axios from "axios";

const Swapi = () => {
  const [hero, setHero] = useState("");

  useEffect(() => {
    const getHero = async () => {
      return String(axios.get("https://swapi.dev/api/people/1").then (r => r.data));
    };
    setHero(getHero());
  }, [hero]);
  return <div>{hero}</div>;
};

export default Swapi;
