import React, { useEffect, useState } from "react";
import Info from "./Info";
import "./App.css";

const Swapi = ({hero, starships}) => {

const [isMounted, setMounted] = useState(false);
const [isVisible, toggleVisible] = useState((p) => (p ? 0 : 1), 0);
const [starship, setStarship] = useState({});

useEffect(() => {
 toggleVisible()
}, [isMounted]);

const onHover = (e) => {
  toggleVisible (!isVisible)
  console.log("id>>>", e.target.id)
  setStarship(starships[e.target.id])
}

  return <div className="swapi">
      <p>{hero}</p>
      <br />
      <ol>{starships.map((starship, i) => (
      <li 
      id={i}
      onMouseOver={onHover} 
      key={starship.name}
      className="holder"
      >{starship.name}
      </li>))}
      </ol>
      {isVisible && (
      <div
      style={{
        fontSize: 10,
        opacity: {isVisible},
        zIndex:10,
      }}
    ><Info  starship={starship} /></div>)}
    </div>;
};

export default Swapi;
