import React, { useEffect, useState, useReducer } from "react";
import Info from "./Info";
import "./App.css";

const Swapi = ({hero, starships}) => {

const [isMounted, toggleMounted] = useReducer((p) => !p, false);
const [isVisible, toggleVisible] = useReducer((p) => (p ? 0 : 1), 0);
const [starship, setStarship] = useState({});

useEffect(() => {
 toggleVisible()
}, [isMounted]);

const onHover = (e) => {
  toggleMounted (!isMounted)
  console.log("id>>>", e.target.id)
  setStarship(starships[e.target.id])
}

  return <div className="swapi">
      <p>{hero}</p>
      <br />
      <ol>{starships.map((starship, i) => (
      <li 
      id={i}
      onMouseOver={onHover} 
      key={starship.name}
      className="holder"
      >{starship.name}
      </li>))}
      </ol>
      <Info  starship={starship} visible={isVisible} />
    </div>;
};

export default Swapi;
