import React, { useState, useEffect } from "react";
import axios from "axios";
import Swapi from "./Swapi";

import "./App.css";

const Main = () => {
  const [hero, setHero] = useState("");
  const [heroId, setHeroId] = useState("1");

  useEffect(() => {
    const getHero = async (id) => {
      let url = `https://swapi.dev/api/people/${id}/`;
      const r = await axios.get(url).then(function (res1) {
        setHero(res1.data.name);
      });
    };
    getHero(heroId);
  }, []);

  const onChange = (e) => {
    setHeroId(e.target.value)
  }

  return (
    <div className="main">
      <input id="number" type="number" onChange={onChange} value="1"></input>
      <Swapi hero={hero} />
    </div>
  );
};

export default Main;
