import React, { useState, useEffect } from "react";
import axios from "axios";

const Swapi = () => {
  const [hero, setHero] = useState("");

  useEffect(() => {
    const getHero = async () => {
        const r =  await axios.get('https://swapi.dev/api/people/1',);
    setHero(r.data.name);
    };
    getHero()
  }, []);

  return <div>{
    hero}
    </div>;
};

export default Swapi;
