import React, {useState, useEffect} from "react";
import CountDown from "./CountDown";
// import StopTimer from "./StopTimer";
// import ButtonPlay from "./ButtonPlay";
import "./App.css";

const Timer = () =>  {

    let hours = 0;
    let minutes = 0;
    let seconds = 0;
        const [paused, setPaused] = useState(false);
        const [over, setOver] = useState(false);
        const [[h, m, s], setTime] = useState([0, 0, 0]);

        const setTimer = () => {
          if (paused || over) return;
          if (h === 0 && m === 0 && s === 0) setOver(true);
          else if (m === 0 && s === 0) {
            setTime([h - 1, 59, 59]);
          } else if (s === 0) {
            setTime([h, m - 1, 59]);
          } else {
            setTime([h, m, s - 1]);
          }
        };

        const reset = () => {
          setTime([parseInt(hours), parseInt(minutes), parseInt(seconds)]);
          setPaused(false);
          setOver(false);
        };

        useEffect(() => {
          const timerID = setInterval(() => setTimer(), 1000);
          return () => clearInterval(timerID);
        });

       return (
          <div>
            <span>{`${h.toString().padStart(2, '0')}:
            ${m.toString().padStart(2, '0')}:
            ${s.toString().padStart(2, '0')}`}</span>

            <button onClick={() => setPaused(!paused)}>
              {paused ? 'Resume' : 'Pause'}
            </button>
            <button onClick={() => reset()}>Restart</button>
            <input className="button_play" type="button" value={"&#xe807"} />
            <input className="time" type="time" id="appt" name="appt" min="00:00" max="24:00" required />
          </div>
        );
      };

