import React, { useState, useEffect } from "react";
import CountDown from "./CountDown";
import StopTimer from "./StopTimer";
import ButtonPlay from "./ButtonPlay";
import "./App.css";

const Timer = () => {
  let countDownTime = new Date();
  let distance = 0;
  let now = new Date();
  let timerId = null;
  let hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
  let minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
  let seconds = Math.floor((distance % (1000 * 60)) / 1000);

  const [paused, setPaused] = useState(false);
  const [over, setOver] = useState(false);
  const [[h, min, sec], setStopTime] = useState([0, 0, 0]);

  const titleButton = [
    { name: "inactive", value: "&#xe807" },
    { name: "active", value: "\ue807" },
    { name: "pause", value: "\u01C1" },
  ];

  const setTimer = () => {
    distance -= 1000;
    if (distance < 0) {
      distance = 0;
      stopTimer();
    }
    let hours = Math.floor(
      (distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
    );
    let minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
    let seconds = Math.floor((distance % (1000 * 60)) / 1000);
    h = hours.toString().padStart(2, "0");
    min = minutes.toString().padStart(2, "0");
    sec = seconds.toString().padStart(2, "0");
  };
  const stopTimer = () => {
    clearInterval(timerId);
  };

  const visualCountDown = () => {
    if (timerId === null) {
      let endDate = new Date();
      let now = new Date();
      let inputHM = setStopTime.value;
      let a = inputHM.split(":");
      endDate.setHours(+a[0]);
      endDate.setMinutes(+a[1]);

      if (endDate.getTime() < now.getTime()) {
        distance = endDate.getTime() - now.getTime() + 1000 * 60 * 60 * 24;
      } else {
        distance = endDate.getTime() - now.getTime();
      };
      useEffect(() => {
        const timerID = setInterval(() => setTimer(), 1000);
        return () => clearInterval(timerID);
      });
    }
    // if (paused || over) return;
    // if (h === 0 && m === 0 && s === 0) setOver(true);
    // else if (m === 0 && s === 0) {
    //   setTime([h - 1, 59, 59]);
    // } else if (s === 0) {
    //   setTime([h, m - 1, 59]);
    // } else {
    //   setTime([h, m, s - 1]);
    // }
  };

  const reset = () => {
    setTime([parseInt(hours), parseInt(minutes), parseInt(seconds)]);
    setPaused(false);
    setOver(false);
  };

  return (
    <div>
      <div className="player">
        <CountDown />

        <ButtonPlay />

        {/* <button onClick={() => setPaused(!paused)}>
              {paused ? 'Resume' : 'Pause'}
            </button>
            <button onClick={() => reset()}>Restart</button> */}
      </div>
      <StopTimer
        setStopTime={{ h, min, sec }}
        onChange={(event) => setTime(event.target.value)}
      />
    </div>
  );
};

