import React from 'react';
import { useState } from "react";
import Button from "./Button";


const Switcher = () => {

    let [count, setCount] = useState(0);

    return   <div>
        <Button a = { count }
        setA = { setCount }
        titleBtn =  {'+'}
        growth = { 1 }
        />  

        <Button a = { count }
        setA = { setCount }
        titleBtn =  {'-'}
        growth = { -1 }
        />

        <input type="text" value={ count } onChange={this.event.target.value} />
    </div>

};
export default Switcher;
