import React from "react";
import { unmountComponentAtNode, render } from "react-dom";
import Master from "./Master";
import Slave from "./Slave";
import "./App.css";

class Main extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
          isToggleOn: false,
          title: "OFF",
          level: 0,
        };
      this.handleClick = this.handleClick.bind(this);
      this.changeLevel = this.changeLevel.bind(this);
    }
    handleClick() {
      this.setState((prevState) => ({
        isToggleOn: !prevState.isToggleOn,
        title: !prevState.isToggleOn ? "OFF" : "ON",
      }));

      if(!this.state.isToggleOn) {
        this.mount();
      } else {
        this.unmount();
      }
    }
    mount(){
      console.log('>>>> render');
      render(<Slave level={this.state.level} />, document.getElementById('a'))
    }
    unmount(){
      console.log('>>>> unmound');
      unmountComponentAtNode(document.getElementById('a'))
    }
    changeLevel(level){
      if(!this.state.isToggleOn) {
        return;
      }
      this.setState({
        level: level,
      })
    }

    render() {
      return (
        <div className="main">
          <Master
            onHandleClick={this.handleClick}
            titleButton={this.state.title}
            onChangeLevel={this.changeLevel}
            level={this.state.level} 
          />
          <div id="a"></div>
        </div>
      );
    }
};

export default Main;
