import React, { useState, useEffect } from "react";
import StopTimer from "./StopTimer";
import ButtonPlay from "./ButtonPlay";
import "./App.css";

const Timer = () => {

  let distance = 0;
  let [timerId, setTimerId] = useState(null);
  const [paused, setPaused] = useState(false);

  let [stopTime, setStopTime] = useState("00:00:00");
  let [endDateString, setEndDateString] = useState("00:00");

  let titleButton = [
    { name: "inactive", value: "\ue807" },
    { name: "active", value: "\ue807" },
    { name: "pause", value: "\u01C1" },
  ];
  let [buttonTitle, setButtonTitle] = useState(titleButton[0].value); 

  const setTimer = () => {
    distance = distance - 1000;
    if (distance < 0) {
        distance = 0;
        stopTimer();
    }
    console.log("tick");
    let hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    let minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
    let seconds = Math.floor((distance % (1000 * 60)) / 1000);
    let comps = [hours.toString().padStart(2, "0"), minutes.toString().padStart(2, "0"), seconds.toString().padStart(2, "0")];
    console.log(comps);
    setStopTime(`${comps[0]}:${comps[1]}:${comps[2]}`);
  };
  const stopTimer = () => {
    clearInterval(timerId);
  };
// useEffect(() => {
//     function update(distance) {
//         setDistance(distance);
//     }
//       }, [distance]);

  const visualCountDown = () => {
    console.log("timerId", timerId);
    if (timerId === null && distance === 0) {
      let endDate = new Date();
      let now = new Date();
      let a = endDateString.split(":");
      console.log("endDateString", endDateString);
      endDate.setHours(+a[0]);
      endDate.setMinutes(+a[1]);
      console.log("start");

      if (endDate.getTime() < now.getTime()) {
        distance = endDate.getTime() - now.getTime() + 1000 * 60 * 60 * 24;
      } else {
        distance = endDate.getTime() - now.getTime();
      };
      setButtonTitle(titleButton[2].value);
      setTimerId(setInterval(() => setTimer(), 1000));
      setPaused(false);
    } else {
        if(paused) {
            setTimerId(setInterval(setTimer, 1000));
            setButtonTitle(titleButton[2].value);
            console.log("resume");
        } else {
            setButtonTitle(titleButton[1].value);
            clearInterval(timerId);
            setTimerId(null);
            console.log("pause");
        }
        setPaused(!paused);
    }
    };

  return (
    <div>
      <div className="player">
        <div className="timer" id="animation">
      <p>{stopTime}</p>
        </div>
        <ButtonPlay click = {visualCountDown}
                    titleButton = {buttonTitle} />

      </div>
      <StopTimer
        onChange={(event) => setEndDateString(event)}
      />
    </div>
  );
};


export default Timer;
