import React from "react";
import { useState } from "react";
import Button from "./Button";

const Switcher = () => {
    let [a, setA] = useState(0);
   const inc = (val) =>  val+1;
   const dec = (val) => val-1;

    return   <div>
        <Button a = { a }
        setA = { setA }
        titleBtn =  {'-'}
        operation = {dec}
        />  
        <br />
        <Button a = { a }
        setA = { setA }
        titleBtn =  {'+'}
        operation = {inc}
        />
        state: { String(a) }
    </div>
};
export default Switcher;
