import React from "react";
import "./App.css";

const CountDown = (h,m,s) => {
  return (
    <div className="timer" id="animation">
      <span>{`${h.toString().padStart(2, "0")}:
    ${m.toString().padStart(2, "0")}:
    ${s.toString().padStart(2, "0")}`}</span>
    </div>
  );
};

export default CountDown;
